    return (
        <div>
            <h2>Hello World!</h2>
            {!changedText && <p>It's good to see you!</p>}
            {changedText && <p style={{ color: 'red' }}>changed</p>}
            <button
                onClick={changeTextHandler}
            >Change text</button>
        </div>
    );
};

export default Greeting;
